export default function createTimeRangeFormatter(formatFunc, metadata) {
  const format = (value) => formatFunc(value);

  if (typeof metadata !== 'undefined') {
    const {
      id,
      label,
      description,
      useLocalTime } =
    metadata;
    format.id = id;
    format.label = label;
    format.description = description;
    format.useLocalTime = useLocalTime;
  }

  return format;
}
