import _keyBy from "lodash/keyBy";


















import { DatasourcesAction } from '../actions/datasources';
export default function datasourcesReducer(datasources, action) {
  if (action.type === DatasourcesAction.SET_DATASOURCES) {
    return {
      ...datasources,
      ..._keyBy(action.datasources, 'uid') };

  }
  if (action.type === DatasourcesAction.SET_DATASOURCE) {
    return {
      ...datasources,
      [action.key]: action.datasource };

  }
  return datasources || {};
}
